	(case when cais.tiplavg=4 then '0' else to_char(cais.tiplavg,'FM9') end) || 
	(case when tab5nord is null then '00000' else lpad(to_char(tab5nord*100,'FM99999'),5,'0') end) || 
	(case when tab5tip is null then '000' else lpad(tab5tip,3,'0') end) || 
