		 when cais.codliv1 is not null then
		 rpad(cais1.descat,30,'*') || rpad(cais.descat,30,'*') || 
		 '******************************' || '******************************' || '******************************'
		 else rpad(cais.descat,30,'*') || '******************************' || 
		 '******************************' || '******************************' || '******************************' end)
